package stegage_test

import (
	"bytes"
	"encoding/base64"
	"strings"
	"testing"

    "github.com/revelaction/stegage"
)

// From https://pkg.go.dev/image/png@go1.17.5#example-Decode
const gopherPng = ``

// From https://pkg.go.dev/image#Image
const pikeCoxJpeg = ``

const password = "73vb$#%51vf89vav9g&*%($C"
const data = "Lorem ipsum dolor sit amet"

func TestEncodeDecode(t *testing.T) {

	var images = []string{gopherPng, pikeCoxJpeg}

	for _, img := range images {

		imageR := base64.NewDecoder(base64.StdEncoding, strings.NewReader(img))

		passR := strings.NewReader(password)
		dataR := strings.NewReader(data)
		outEnc := new(bytes.Buffer)

		if err := stegage.Encode(passR, dataR, imageR, outEnc); err != nil {
			t.Fatal(err)
		}

		outDec := new(bytes.Buffer)

		passR = strings.NewReader(password)
		if err := stegage.Decode(passR, outEnc, outDec); err != nil {
			t.Fatal(err)
		}

		if string(outDec.Bytes()) != data {
			t.Errorf("wrong data: %q, expected %q", outDec.Bytes(), data)
		}

	}
}

func TestDecodeImageWithoutEncryptedFile(t *testing.T) {

	imageR := base64.NewDecoder(base64.StdEncoding, strings.NewReader(pikeCoxJpeg))

	out := new(bytes.Buffer)

	passR := strings.NewReader(password)
	if err := stegage.Decode(passR, imageR, out); err == nil {

		t.Fatal("Uncaught error: Image without encrypted age payload")
	}
}

func TestDecodeWrongPassword(t *testing.T) {

	imageR := base64.NewDecoder(base64.StdEncoding, strings.NewReader(pikeCoxJpeg))

	passR := strings.NewReader(password)
	dataR := strings.NewReader(data)
	outEnc := new(bytes.Buffer)

	if err := stegage.Encode(passR, dataR, imageR, outEnc); err != nil {
		t.Fatal(err)
	}

	outDec := new(bytes.Buffer)

	wrongPassR := strings.NewReader("wrong password")
	if err := stegage.Decode(wrongPassR, outEnc, outDec); err == nil {
		t.Fatal("Uncaught error: Wrong password")
	}
}

func TestEncodeEmptyPassword(t *testing.T) {

	imageR := base64.NewDecoder(base64.StdEncoding, strings.NewReader(gopherPng))

	passR := strings.NewReader("")
	dataR := strings.NewReader(data)
	outEnc := new(bytes.Buffer)

	if err := stegage.Encode(passR, dataR, imageR, outEnc); err == nil {
		t.Fatal("Uncaught error: Empty password")
	}
}

func TestDecodeEmptyPassword(t *testing.T) {

	imageR := base64.NewDecoder(base64.StdEncoding, strings.NewReader(pikeCoxJpeg))

	passR := strings.NewReader(password)
	dataR := strings.NewReader(data)
	outEnc := new(bytes.Buffer)

	if err := stegage.Encode(passR, dataR, imageR, outEnc); err != nil {
		t.Fatal(err)
	}

	outDec := new(bytes.Buffer)

	emptyPassR := strings.NewReader("")
	if err := stegage.Decode(emptyPassR, outEnc, outDec); err == nil {
		t.Fatal("Uncaught error: Empty password")
	}
}

